
const Console = console.Console;
const CONSOLE_LOG = true;
const DEBUG = false;
const INFO = false;
const FILE_LOG = true;

log = function log(message) {
	if (CONSOLE_LOG) {
		console.log(getBlue(message));
	}
}
//function foo(a, b, ...others) {
//    console.log("a and b are ", a, b);
//
//    for (let val of others) {
//        console.log(val);
//    }
//}
debug = function debug(message) {
	if (DEBUG) {
		message = '[DEBUG] ' + message;
		log(message);
	}
}

info = function info(message) {
	if (INFO) {
		message = '[INFO] ' + message;
		log(message);
	}
}

error = function error(message) {
	if (CONSOLE_LOG) {
		console.error(new Error(message));
	}
};

function getRed(message){
	return '\033[0;91m'+message+'\033[0;0m';
}

function getBlue(message){
	return '\033[0;94m'+message+'\033[0;0m';
}

function getGreen(message){
	return '\[\033[0;92m\]'+message+'\[\033[0;0m\]';
}

function getYellow(message){
	return '\[\033[0;93m\]'+message+'\[\033[0;0m\]';
}

function getCyan(message){
	return '\[\033[0;96m\]'+message+'\[\033[0;0m\]';
}


module.exports.log = log;
module.exports.error = error;
module.exports.debug = debug;

